function ArtsyCloud({ index }: { index: number }) {
  return (
    <div className="cloud-background__cloud cloud-background__body-mark">
      <img src={`${PUBLIC_URL.ROOT}cloud-arte-ruim-${index}.jpg`} alt="cloud" />
    </div>
  );
}

function getTypeComponent(type: string) {
  switch (type) {
    case 'cloud':
      return Cloud;
    case 'cyber-cloud':
      return CyberCloud;
    case 'funky-cloud':
      return FunkyCloud;
    case 'detective-cloud':
      return DetectiveCloud;
    case 'artsy-cloud':
      return ArtsyCloud;
    default:
      return Cloud;
  }
}

type CloudBackgroundProps = {
  type?: 'cloud' | 'cyber-cloud' | 'funky-cloud' | 'detective-cloud' | 'artsy-cloud';
};

export function CloudBackground({ type = 'cloud' }: CloudBackgroundProps) {
  const TypeComponent = getTypeComponent(type);

  const baseClass = 'cloud-background';

  return (
    <div className={clsx(baseClass, `${baseClass}--${type}`)}>
      <div className="cloud-background__animation-1">
        <TypeComponent index={0} />
      </div>

      <div className="cloud-background__animation-2">
        <TypeComponent index={1} />
      </div>

      <div className="cloud-background__animation-3">
        <TypeComponent index={2} />
      </div>

      <div className="cloud-background__animation-4">
        <TypeComponent index={3} />
      </div>

      <div className="cloud-background__animation-5">
        <TypeComponent index={4} />
      </div>
    </div>
  );
}
